sys_variable_value.value:
(function execute(inputs, outputs) {
 
var jsonObj=inputs.res.Person;
//gs.info(jsonObj);
 
var obj={};
var res=[];
 
jsonObj.forEach(function(element){
obj={};
obj.documents=[];
var documentArray=element.document;
 
documentArray.forEach(function(ele){
var doc={
    docName:ele.name||"",
    reasonDoc:ele.reason||"",
    statusDoc:ele.status ||""
}
obj.documents.push(doc);
});
obj.personName=element.pname;
obj.ptype=element.ptype;
res.push(obj);
});
gs.info(JSON.stringify(res))
outputs.person=res;
})(inputs, outputs);

