var iciciFieldValidationAppSubsds = Class.create();
iciciFieldValidationAppSubsds.prototype = {
    initialize: function() {},
    validate: function(obj, txnObj) {
        var txnReq = JSON.parse(txnObj);
        var jsonObj = (obj);
        try {

            // if (txnObj.isProposer == "true") {
            //     //this means dependent flag is true

			//	if(txnReq.isProposer === "true")
               // proposerInfoCheck(jsonObj.data);

                lifeAssuredCheck(jsonObj.data);



            // } else {
            //     lifeAssuredCheck(jsonObj.data);

            // }
            //}
            if (jsonObj.data.nomineeInfos && jsonObj.data.nomineeInfos.length > 0) {
                var count = 1;
                jsonObj.data.nomineeInfos.forEach(function(nominee, count) {
                    nomineeeCheck(nominee, count);
                    count++;
                });
            }
            otherFieldsCheck(jsonObj.data);
            return JSON.stringify({
                code: 200,
                msg: "correct"
            });
        } catch (ex) {
            return JSON.stringify({
                code: 400,
                msg: ex.message
            });
        }

    },

    type: 'iciciFieldValidationAppSubsds'
};

