sys_variable_value.value:
(function execute(inputs, outputs) {
 
 
if (inputs.insurer== "HDFC" ){
   var ipphsame= JSON.parse(inputs.txnReq).isProposer;
   var pHNRI =JSON.parse(inputs.txnReq).isNri;
   var lAnri= inputs.request.data.suitabilityMatrix.isLANri || "false";
  // var lAnri= inputs.request.data.suitabilityMatrix.nationalty || "Indian";
   var req= (inputs.request);
 
//    gs.info(ipphsame)
    // gs.info(pHNRI + "phnri")
    //  gs.info(lAnri + "lanri")
// //    gs.info(req)
 
 
 if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
     
}
if(ipphsame=="No"){
    if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
        }
 }
 
    if(ipphsame=="Yes" && pHNRI=="Y"){
        if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
     
        }
        else if(req.data.lifeAssrdInfos.comunctnAddress.country =="India"){
     
           outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.comunctnAddress.country should be not be India in case of NRI";
            return
        }
    }
 
 
    // if(ipphsame=="No" && lAnri!="Indian")
    if(ipphsame=="No" && lAnri!="false"){
        if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
        }
        else if(req.data.lifeAssrdInfos.comunctnAddress.country =="India"){
             outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.comunctnAddress.country should be not be India in case of NRI";
            return
        }
 
    }
 
    if(ipphsame=="No" && pHNRI=="Y"){
        if(req.data.proposerInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.proposerInfos.prmntAddress.country should be India incase of NRI";
            return
        }
        else if(req.data.proposerInfos.comunctnAddress.country =="India"){
            outputs.code=400;
            outputs.msg="data.proposerInfos.comunctnAddress.country should be not be India in case of NRI";
            return
        }
 
    }
 
    var res =  new global.hdfcFieldValidationAppSub().validate((inputs.request),inputs.txnReq);
    res=JSON.parse(res);
    outputs.code=res.code;
    outputs.msg=res.msg;
}
 
else if (inputs.insurer== "ICICI" ){
 
   var ipphsame= JSON.parse(inputs.txnReq).isProposer;
   var pHNRI =JSON.parse(inputs.txnReq).isNri;
   var lAnri= inputs.request.data.suitabilityMatrix.isLANri || "false"
  // var lAnri= inputs.request.data.suitabilityMatrix.nationalty || "Indian";
   var req= (inputs.request);
 

  if(ipphsame=="false" && pHNRI=="true"){//ip==ph && NRI  IS TRUE
      // gs.info(req.data.lifeAssrdInfos.prmntAddress.country + "validn")
        if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
     
        }
        else if(req.data.lifeAssrdInfos.comunctnAddress.country =="India"){
     
           outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.comunctnAddress.country should be not be India in case of NRI";
            return
        }
    }
 
 
    if(ipphsame=="true" && lAnri!="false"){ //iP!=PH && LA IS NRI
        if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
        }
        else if(req.data.lifeAssrdInfos.comunctnAddress.country =="India"){
             outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.comunctnAddress.country should be not be India in case of NRI";
            return
        }
 
    }
 
    if(ipphsame=="true" && pHNRI=="true"){ //ip!=ph && ph in nri
        if(req.data.proposerInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.proposerInfos.prmntAddress.country should be India incase of NRI";
             return
        }
        else if(req.data.proposerInfos.comunctnAddress.country =="India"){
            outputs.code=400;
            outputs.msg="data.proposerInfos.comunctnAddress.country should be not be India in case of NRI";
             return
        }
 
    }
 //gs.info("txnreq......"+inputs.txnReq);
 
    var res =  new global.iciciFieldValidationAppSub().validate((inputs.request),inputs.txnReq);
    res=JSON.parse(res);
    outputs.code=res.code;
    outputs.msg=res.msg;
}
 
 else if (inputs.insurer== "BALIC" ){
 
var ipphsame= JSON.parse(inputs.txnReq).isProposer;
   var pHNRI =JSON.parse(inputs.txnReq).isNri;
   //var lAnri= inputs.request.data.suitabilityMatrix.nationalty || "Indian";
   var lAnri= inputs.request.data.suitabilityMatrix.isLANri || "false";
   var req= (inputs.request);
   var req= (inputs.request);
  pHNRI = (pHNRI=="NGST")?"N":pHNRI;

 //  gs.info("1");
    if(ipphsame=="Y" && pHNRI=="N"){
        if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
     
        }
        // else if(req.data.lifeAssrdInfos.comunctnAddress.country =="India"){
     
        //    outputs.code=400;
        //     outputs.msg="Communication address should be not be India in case of NRI";
        //     return
        // }
    }
 
  // gs.info("2");
 
    if(ipphsame=="Y" && lAnri!="false"){
        if(req.data.lifeAssrdInfos.prmntAddress.country !="India"){
            outputs.code=400;
            outputs.msg="data.lifeAssrdInfos.prmntAddress.country should be India incase of NRI";
            return
        }
        // else if(req.data.lifeAssrdInfos.comunctnAddress.country =="India"){
        //      outputs.code=400;
        //     outputs.msg="Communication address should be not be India in case of NRI";
        //     return
        // }
 
    }
 //  gs.info("3");
 
    if(ipphsame=="N" && pHNRI=="N"){
        if(req.data.proposerInfos.prmntAddress.country !="India"){
         outputs.code=400;
            outputs.msg="data.proposerInfos.prmntAddress.country should be India incase of NRI";
            return
        }
        // else if(req.data.proposerInfos.comunctnAddress.country =="India"){
        // outputs.code=400;
        //     outputs.msg="Communication address should be not be India in case of NRI";
        //     return
        // }
 
 
    }
 
 
    //gs.info("script"+JSON.stringify(inputs.request));
    var res =  new global.balicFieldValidationAppSub().validate((inputs.request),inputs.txnReq);
    //gs.info(res);
 
    res=JSON.parse(res);
    outputs.code=res.code;
    outputs.msg=res.msg;
   
}
 
})(inputs, outputs);

