sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var jObj=(inputs.request);
// gs.info("1"+jObj);
// gs.info("2"+JSON.stringify(jObj.funds.length))
if(!jObj.funds.name){
 var funds= [
            {
                "name": "Multi Cap Growth Fund",
                "value": "65"
            },
            {
                "name": "Income Fund",
                "value": "35"
            }
        ]
  gs.info(funds)
  jObj.funds= funds
}
gs.info("3"+jObj.funds);

var builder = new sn_ih.XMLStreamingBuilder().build();
//funds Allocation
builder.startElement("FundAllocation");
  jObj.funds.forEach(function(element){
    builder.startElement("Fund");
    builder.writeTextElement("Name",element.name);
    builder.writeTextElement("Value",element.value);
    builder.endElement();
  });
    builder.endElement();

var req=builder.getXMLString();
//gs.info("1"+req);

     var xmlDoc = new XMLDocument2();
     xmlDoc.parseXML(req); 
     var xmlString = req.toString();
     var xmlClean = global.JSUtil.unescapeText(xmlString);
          
outputs.funds =xmlClean.toString().replace(/<\?xml.*\?>/g,'').replace(/<FundAllocation>|<\/FundAllocation>/g, '');


// var xml = new sn_ih.XMLStreamingBuilder().build();
// xml.startDocument("Employee")
//   .writeArray(["Name","Value"], jObj.funds, 'funds')
// .endDocument();


// var xml = new sn_ih.XMLStreamingBuilder().build();
// xml.startDocument("Funds Allocation");
//   jObj.funds.forEach(function(element){
//     xml.startElement("Funds");
//     xml.writeTextElement("Name",element.name);
//     xml.writeTextElement("Value",element.nalue);
// xml.endElement()
//   })
// xml.endDocument();

// outputs.pay= xml.getXMLString();
})(inputs, outputs);

