sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

var jObj=inputs.request;

//gs.info(jObj.product.coverDeatils.riderDetails.length)
if(!jObj.riders || !jObj.riders[0] || jObj.riders[0].riderCoverCode==""){
outputs.rider="";
return;
}
else{
  var builder = new sn_ih.XMLStreamingBuilder().build();
jObj.riders.forEach(function(element){
builder.startElement("Rider")
      builder.writeTextElement("Name",element.riderCoverCode)
      builder.writeTextElement("SA",element.riderSumAssured)
      builder.writeTextElement("Term", element.riderPolicyterm)
    builder.endElement()     
     })

var req= builder.getXMLString();
 var xmlDoc = new XMLDocument2();
   //  gs.info("0: " + req);
     xmlDoc.parseXML(req); 
     var xmlString = req.toString();
     var xmlClean = global.JSUtil.unescapeText(xmlString);
    // gs.info("4: "+xmlClean)
     
     outputs.rider=xmlClean.toString().replace(/<\?xml.*\?>/g,'');
}


})(inputs, outputs);

