sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var jObj=JSON.parse(inputs.req);
var prod=inputs.xml;

var builder = new sn_ih.XMLStreamingBuilder().build();

builder.startDocument("EBIRequest")
    .enablePrettyPrint()
    .writeTextElement("FirstName",jObj.fName||"")
    .writeTextElement("LastName",jObj.lName ||"")
    //.writeTextElement("age",jObj.insuredPerson.age)
    .writeTextElement("DateOfBirth",jObj.dateOfBirth)
    .writeTextElement("Gender",jObj.gender)
    .writeTextElement("MaritalStatus",jObj.maritalStatus||"")
 //   .writeTextElement("PolicyholderFName",jObj.policyHolder.firstName)
   // .writeTextElement("PolicyholderLName",jObj.policyHolder.lastName)
   // .writeTextElement("PolicyholderDOB",jObj.policyHolder.dob)
    //.writeTextElement("PolicyholderGender",jObj.policyHolder.gender)
    //.writeTextElement("isNRI",jObj.nri)
    .writeTextElement("isNRI",jObj.isNri)

    //.writeTextElement("isPolicyholder",jObj.existingCustomer)
  //  .writeTextElement("Staff",jObj.insuredPerson.staff)
   // .writeTextElement("AgeProof",jObj.insuredPerson.ageProof)
    .writeTextElement("ServiceTaxNotApplicable",0)
//    .writeTextElement("AreaCode",jObj.insuredPerson.ageProof)
.writeTextElement("ProductDetails",prod)


   builder.endDocument()

var req=builder.getXMLString();


     var xmlDoc = new XMLDocument2();
     //gs.info("0: " + req);
     xmlDoc.parseXML(req); 
     var xmlString = req.toString();
     var xmlClean = global.JSUtil.unescapeText(xmlString);
     //gs.info("4: "+xmlClean)
     xmlDoc = new XMLDocument2();
     
     outputs.xml=xmlClean.toString().replace(/<\?xml.*\?>/g,'');


})(inputs, outputs);

