sys_variable_value.value:
(function execute(inputs, outputs) {
   var res;
  if(!(inputs.response.STATUS) && !inputs.response.code ){
   	res= JSON.parse(inputs.response);
  }
  else{
     res= (inputs.response);
  }
  	//flowvariable+=res;

  if(!inputs.fvressp.responses)
  	var resp = JSON.parse(inputs.fvressp);
  else
    var resp =(inputs.fvressp);

  resp.responses.push(res);
  outputs.response = resp;
  inputs.fvressp=resp;
	// ... code ...
})(inputs, outputs);

