sys_variable_value.value:
 (function execute(inputs, outputs) {
// ... code ...
    if(inputs.sum && (parseInt(inputs.sum)!=0 || (inputs.sum)!="0.0")){
        if(inputs.sum>inputs.premium*inputs.max){
     //   outputs.max=inputs.premium*inputs.max +"";
        outputs.valid= false;
    }
    else{
    outputs.valid= true;
    outputs.max=inputs.sum;
    }
    }
    else{
        outputs.valid= true;
        outputs.max=inputs.premium*inputs.max;
    }
})(inputs, outputs);

