sys_variable_value.value:
(function execute(inputs, outputs) {

    var req;
    if (!inputs.request.ipPhSame) {
        req = JSON.parse(inputs.request);
    } else {
        req = inputs.request;
    }

    var array = req.product.funds;
    gs.info(JSON.stringify(array)); 


    outputs.response = req;
    outputs.ip = req.ipPhSame;
    outputs.fre = req.product.frequency;
    outputs.gen = req.insuredPerson.gender;
    outputs.occ = req.insuredPerson.occupation;
    outputs.pro = req.proposalType;
    //outputs.strat = req.product.investmentProtifolio;


    if (req.product.policyTerm===req.product.policyPaymentTerm){
    outputs.ppt = "Regular";
    }
    else{
        outputs.ppt = "Limited";
    }

    var funds = [];

   if(!array)
   return
    array.forEach(function(rec) {
        var iGr = new GlideRecord("x_rptp_smartinte_0_integrationinsurer");
        iGr.addQuery("code", inputs.insurer);
        iGr.query();
        if (iGr.next()) {
            var oGr = new GlideRecord("x_rptp_smartinte_0_integrationoptiondatatypemaster");
            oGr.addQuery("option", rec.name);
            oGr.query();
            if (oGr.next()) {
                var gr = new GlideRecord('x_rptp_smartinte_0_integration_option_mapper');
                gr.addQuery("insurer", iGr.sys_id);
                gr.addQuery("option", oGr.sys_id);
                gr.query();

                if (gr.next()) {
                    var name = gr.getValue('value'); 
                    rec.name = name;

                    if (inputs.insurer === "HDFC") { 
                        var nameCode = name.toUpperCase().replace(/ /g, '_'); 
                        rec.nameCode = nameCode;
                    }
                    funds.push(rec);
                }
            }
        }
    });


    gs.info(JSON.stringify(funds));

    outputs.funds = funds;
    // outputs.funds = JSON.stringify(funds);

})(inputs, outputs);

