sys_variable_value.value:
(function execute(inputs, outputs) {



// ... code ...
  var req=inputs.req;
  req.ipPhSame=inputs.ip;
  req.product.frequency=inputs.frequency;
  req.proposalType=inputs.proposal;
  req.insuredPerson.gender=inputs.gender;
  req.insuredPerson.occupation=inputs.occ;
  //req.product.investmentProtifolio=inputs.investmentProtifolio;
  req.product.pptOption=inputs.ppt;
  req.product.funds=inputs.funds;

  req.product.annualPremium=calculateAnnualPremium(req.product.frequency,req.product.premium);

req.insuredPerson.age=calculateAge(req.insuredPerson.dob);
//gs.info(calculateAge(req.insuredPerson.dob));
  outputs.requestbody=req;


})(inputs, outputs);

 function calculateAge(dob) {
    if (!dob) {
        gs.error("Date of Birth is not provided.");
        return;
    }

    var dobGdt = new GlideDateTime();
    dobGdt.setDisplayValue(dob); // Assuming dob is in the user's display format

    var currentGdt = new GlideDateTime();

    var dobYear = dobGdt.getYearLocalTime();
    var currentYear = currentGdt.getYearLocalTime();

    var age = currentYear - dobYear;

    var dobMonth = dobGdt.getMonthLocalTime(); // January is 0
    var currentMonth = currentGdt.getMonthLocalTime();

    var dobDay = dobGdt.getDayOfMonthLocalTime();
    var currentDay = currentGdt.getDayOfMonthLocalTime();

    if (currentMonth < dobMonth || (currentMonth == dobMonth && currentDay < dobDay)) {
        age--;
    }

    return age;
}

function calculateAnnualPremium(freq,premium){
  if(freq=="Yearly"){
    return (1* parseInt(premium)).toString().split(".")[0];
  }
  else if(freq=="Monthly"){
    return (12* parseInt(premium)).toString().split(".")[0];

  }
  else if(freq="Single"){
    return (1* parseInt(premium)).toString().split(".")[0];

  }
  else if(freq="Half Yearly"){
    return (2* parseInt(premium)).toString().split(".")[0];

  }
  else if(freq="Quartely"){
    return (4* parseInt(premium)).toString().split(".")[0];

  }
}

