sys_variable_value.value:
(function execute(inputs, outputs) {

    if(!inputs.ptValid && !(inputs.pt !== null &&inputs.pt.length > 0)) {
        outputs.errorflag = true;
        outputs.errormessage = "Invalid PT value";
    }
    else{
      //  gs.info("hey");
        var req=(inputs.request)
        req.pt=(inputs.pt);
        outputs.req=req;
        outputs.errorflag = false;
    }

})(inputs, outputs);

