sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var req=inputs.req;
if (req.productId){
    req=req;
}
else{
    req=JSON.parse(req)
}
if(req.variantId=="VRN003"){
    req.swpFlag="Y";
    if(!req.swpYears){
    req.swpYears=req.policyTerm - 4;
    }
}
outputs.req=req;
})(inputs, outputs);

