function validateFromTable(answer, tableName, fieldName) {
    var gr = new GlideRecord(tableName);
    gr.addQuery('SourceFieldMap', fieldName);  
    gr.addQuery('Option', answer);    
    gr.query();
    return gr.next();
}


        
        
        

function validateradiobutton(answer){
     if (answer == 'Yes' || answer == 'No') {
        return true; 
    } else {
        return false;
    }
}


    function validatetextlength(text, minLength, maxLength) {
    if (text.length >= minLength && text.length <= maxLength) {
        return false;
    } else {
        return true;
    }
}

    function validatemultiselectoption(input, allowedOptions) {
    return allowedOptions.indexOf(input) === -1;
    }

}
