      req.data.proposerInfos.occ=mapper(insurer,pocc,"Occupation","data.proposerInfos.occ");

    var pmrtlSts=req.data.proposerInfos.mrtlSts;
    req.data.proposerInfos.mrtlSts=mapper(insurer,pmrtlSts,"maritalStatus","data.proposerInfos.mrtlSts");

    var ptitle=req.data.proposerInfos.title;
    req.data.proposerInfos.title=mapper(insurer,ptitle,"Title","data.proposerInfos.title");

    var proposergender=req.data.proposerInfos.gender;
     req.data.proposerInfos.gender=mapper(insurer,proposergender,"1st life gender","data.proposerInfos.gender");


     if(nriPH=="true"){
          var rstSts="NRI/PIO"
        }
        else{
          var rstSts="INDIAN"
        }
   // var rstSts=req.data.proposerInfos.rstSts;
      req.data.proposerInfos.rstSts=mapper(insurer,rstSts,"Citizenship","data.proposerInfos.rstSts");
    }
    }

       outputs.reqs=req;
}
catch(ex){
  var msg=ex.message;
  outputs.error=400;
  outputs.msg=msg+"";
  return;
}



