// // new option mapper
function mapper(insurerValue,optionValue,sourceValue,path){
var sourceId="";
var insurerId="";
var optionId="";


// var sourceValue="1st life gender";
// var insurerValue="BALIC";
// var optionValue="G003"

if(optionValue=="" || ! optionValue)
return ""

var sourceGr=new GlideRecord("x_rptp_smartinte_0_integration_source_field_mapper");
sourceGr.addQuery("name",sourceValue);
sourceGr.query();
if(sourceGr.next()){
    sourceId=sourceGr.getValue("sys_id");
}
var insureGr=new GlideRecord("x_rptp_smartinte_0_integrationinsurer");
insureGr.addQuery("code",insurerValue);
insureGr.query();
if(insureGr.next()){
      insurerId=insureGr.getValue("sys_id");
}
var optionGr=new GlideRecord("x_rptp_smartinte_0_integrationoptiondatatypemaster");
optionGr.addQuery("sourcefieldmap",sourceId);
optionGr.addQuery("option",optionValue);
optionGr.query();
if(optionGr.next()){
    optionId=optionGr.getValue("sys_id");
}

//gs.info(optionId+" "+optionValue);

var gr = new GlideRecord('x_rptp_smartinte_0_integration_option_mapper');

    gr.addQuery("option",optionId);
    gr.addQuery("sourcefieldmap",sourceId);
     gr.addQuery("insurer",insurerId);
    gr.query();
    if (gr.next()) {
        return gr.getValue("value");
    }
    else{
        throw new Error("Please enter a valid value for "+path);
    }
  }


})(inputs, outputs);

