 function mapper(insurer, option, fieldName, path) { 
      if(!path)
      path="";
      
      if(option=="")
      return"";
    if(option != ''|| !option) {
         for (var i = 0; i < arr.length; i++){
            if (arr[i].insurer==insurer && arr[i].source == fieldName && arr[i].option == option) {
                var name= arr[i].value;
            return name;
            }    
        }
         throw new Error("Please enter a valid value for - " + path );
      }
    }
   
outputs.res=req;
