sys_variable_value.value:
    (function execute(inputs, outputs) {
    var fArr=inputs.fund.productDetails.fundDetails;
    var array=[];
    //outputs.fundDetails_output={};
    fArr.forEach(function(fundDetail_obj){
     array.push({
        "stringval1":fundDetail_obj.fundName||"",
        "stringval2":fundDetail_obj.percentageAllocation||""
    })
})
//gs.info(array);
//gs.info("111:: "+JSON.stringify(array));


var fundDetails_output={
"WeoRecStrings150User":array
}

//gs.error("2:"+fundDetails_output);
gs.error("2:"+JSON.stringify(fundDetails_output));

outputs.fund_details = (fundDetails_output);
})(inputs, outputs);

