sys_variable_value.value:
(function execute(inputs, outputs) {
    var prodId =JSON.parse(inputs.productId);
    // gs.info("The product ID id " +prodId);
    var gr = new GlideRecord('x_rptp_smartinte_0_integrationproductvariants');
    gr.addQuery('sys_id', prodId);
    gr.query();
       if(gr.next()){
           var arr = [];
           gs.info(gr.variant_id);
           outputs.varId = arr.push(gr.variant_id);
        //    gs.log(outputs.varId);
}
})(inputs, outputs);

