sys_variable_value.value:
(function execute(inputs, outputs) {
    var prodId =(inputs.productId);
    gs.info("The product ID id " +prodId);
var gr = new GlideRecord('x_rptp_smartinte_0_integrationproductvariants');
gr.addQuery('sys_id', prodId);
gr.query();
while(gr.next()){
outputs.varId = gr.variant_id;
}
})(inputs, outputs);

