sys_variable_value.value:
(function execute(inputs, outputs) {
  var jsonObj = (inputs.req);
  var persons = jsonObj.persons;
  var obj = {};
  var res = [];
 
  persons.forEach(function (element) {
    obj = {};
    var documentsArray = element.Documents;
    obj.Documents = [];
    documentsArray.forEach(function (ele) {
      var array = {
        BodyString: ele.docbyteArray,
        Name: ele.docName || "",
        Type: ele.docType || "",
        FileExt: ele.fileExtension || "",
        ShortName: ele.docshortName || "",
      };
      obj.Documents.push(array)
    });
   
    obj.Name = element.Name;
    obj.Type = element.Type;
    res.push((obj));
  });
  var inputRequest={};
  inputRequest.Request = {
      "RequestFrom": jsonObj.vendorName,
      "Request_Value": jsonObj.applicationNo,
      "RequestType": "ApplicationNo"
  };
  inputRequest.Persons =(res);
  outputs.response = inputRequest;
})(inputs, outputs);

