sys_variable_value.value:
(function execute(inputs, outputs) {
var req;
if(inputs.req.productId){
req=inputs.req;
}
else
req=JSON.parse(inputs.req);

if(req.productId=="0cf1bf52ca864819914ea1dffc5890d3"){
    if(req.sumAssured){
        req.sumAssured=req.sumAssured;
    }
    else if(req.age<45){
        req.sumAssured = 10 * parseInt(req.annualPremium) > 0.5* parseInt(req.policyTerm)* parseInt(req.annualPremium) ? 10 * parseInt(req.annualPremium) +"":0.5* parseInt(req.policyTerm)* parseInt(req.annualPremium) +"";
}
else{
     req.sumAssured = 7 * parseInt(req.annualPremium) > 0.25 * parseInt(req.policyTerm) * parseInt(req.annualPremium) ? 7 * parseInt(req.annualPremium)  +"": parseInt(req.policyTerm) * parseInt(req.annualPremium)+"";
    }   
}
else{
    if((req.sumAssured!=""||parseInt(req.sumAssured)!=0.0)){
    }
    else{

   // req.sumAssured=req.annualPremium* req.multiplier +"";
    



 var sum=req.annualPremium* req.multiplier +"";
//  req.sumAssured=sum;

 if(sum<=inputs.sumAssured){
    req.sumAssured=sum;
 }
 else{
    req.sumAssured=inputs.sumAssured
 }
    
if(inputs.isBulk){
    var gr =new GlideRecord('x_rptp_smartinte_0_integration_values_validator_tables');
    gr.addQuery('product_id',req.productId+"");
    gr.addQuery('variant_id',req.variantId+"");
    gr.addQuery('insuredperson',req.isProposer=="Yes"?true:false);
    gr.addQuery('is_nri',req.isNri=="N"?false:true);
    gr.query();
if(gr.next()){
    if(parseInt(inputs.sumAssured) < parseInt(gr.sum_assured)){
        req.sumAssured=gr.sum_assured+"";
    }
    else if(parseInt(inputs.sumAssured) > parseInt(gr.max_sum_assured)){
        req.sumAssured=(gr.max_sum_assured)+"";
    }
    else{
        req.sumAssured=inputs.sumAssured+"";
      }
  //  req.sumAssured=gr.sum_assured+"";
    }
}
  }
    
}
if(req.productId=="0550dee2331e4517ba2624e1a289e695"){
  //  gs.info(req.age);
  //  gs.info(req.annualPremium);


    if(!req.multiplier){
        if(parseInt(req.age)<50 && parseInt(req.annualPremium)<250000){
       // gs.info("hello");
        req.multiplier=10;
        req.sumAssured=10 * parseInt(req.annualPremium);
    }
    else if(parseInt(req.age)<50 && parseInt(req.annualPremium)>=250000){
       // gs.info("hey");

        req.multiplier=7;
        req.sumAssured= 7 * parseInt(req.annualPremium);
        }
        else if(parseInt(req.age)>=50&& parseInt(req.annualPremium)<250000){
      //  gs.info("hellooqqqq2");

        req.multiplier=7;
        req.sumAssured= 7 * parseInt(req.annualPremium);
        }
        else if(parseInt(req.age)>=50 && parseInt(req.annualPremium)>=250000){
      //  gs.info("rgtuiop");

        req.multiplier=5;
        req.sumAssured= 5 * parseInt(req.annualPremium);
        }
        else{
         //   gs.info("nor");
        }
    }

}

outputs.req=req;
})(inputs, outputs);

