sys_variable_value.value:
(function execute(inputs, outputs) {
var errorResponse = {
        status: 'error',
        code: inputs.errorCode,
        message: inputs.errorMessage
    };
    
    outputs.customErrorResponse = JSON.stringify(errorResponse);
})(inputs, outputs);

