sys_variable_value.value:
(function execute(inputs, outputs) {
   
    if(!inputs.pptValid && !(inputs.ppt !== null && inputs.ppt.length > 0)) {
        outputs.errorflag = true;
        outputs.errormessage = "Invalid PPT value";
     }
     else {
        var req=(inputs.request)
        req.ppt=(inputs.ppt);
        outputs.request=req;
        outputs.errorflag = false;
     }
})(inputs, outputs);

