sys_variable_value.value:
(function execute(inputs, outputs) {
    var res = inputs.response;
    var combinedResponses = [];
    var scount = 0; // Success count
    var fcount = 0; // Failure count

    for (var i = 0; i < res.responses.length; i++) {
        var currentResponse = res.responses[i];
        var found = false;

        // Check for the status property and increment the counters
        if (currentResponse.status === "Success") {
            scount++;
        } else {
            fcount++;
        }

        // Add variantId if present in the response
        if (currentResponse.productId && currentResponse.calculations && currentResponse.calculations.length > 0) {
            for (var j = 0; j < combinedResponses.length; j++) {
                if (combinedResponses[j].productId === currentResponse.productId) {
                    if (currentResponse.calculations[0]) {
                        var calculationWithVariant = Object.assign({}, currentResponse.calculations[0]);
                        calculationWithVariant.variantId = currentResponse.variantId || null; // Add variantId or set to null if not present
                        combinedResponses[j].calculations.push(calculationWithVariant);
                        found = true;
                        break;
                    }
                }
            }
        }

        if (!found) {
            // Clone the current response to avoid modifying the original object
            var responseClone = JSON.parse(JSON.stringify(currentResponse));
            // Add variantId to each calculation
            for (var k = 0; k < responseClone.calculations.length; k++) {
                responseClone.calculations[k].variantId = currentResponse.variantId || null;
            }
            // Remove status and variantId from the main object
            delete responseClone.status;
            delete responseClone.variantId;

            combinedResponses.push(responseClone);
        }
    }

    // Include the combined responses and the success/failure counts in the output
    outputs.response = JSON.stringify({
        combinedResponses: combinedResponses,
        scount: scount,
        fcount: fcount
    });
    outputs.res=JSON.stringify(combinedResponses);
})(inputs, outputs);

