(function execute(inputs, outputs) {
// ... code ...
if(inputs.ppt==inputs.pt){
    try{
	var input={};
  	input['u_pt'] = inputs.pt; // Integer 
	input['u_age'] = inputs.age; // Integer 
	input['u_islp'] = inputs.isLp; // True/False 
	input['u_productid'] = inputs.productId; // String 
	input['u_variantid'] = inputs.variantId; // String 

	var dt = new sn_dt.DecisionTableAPI();
  	var response = dt.getDecision('a3356fd52bde12d0c692feb2f291bf40', input);
  
	var result_elements = response.result_elements;
 	var u_max_multiplier = result_elements.u_max_multiplier.getValue();  // Integer
	// gs.info("multi"+u_max_multiplier);
	// gs.info("IPmulti"+inputs.multiplier);

    if(parseInt(inputs.multiplier) > u_max_multiplier)
    {
		outputs.code=400;
    	outputs.message= "Invalid value passed for Multiplier.Max Multiplier Value for this combination is "+u_max_multiplier;
    }

	}
catch(e){
	gs.info("Couldn't run this script Error: "+e);
}

