sys_variable_value.value:
(function execute(inputs, outputs) {
    var req=inputs.req;
    if(typeof req =='object'){
        req=req;
    }else{
        req=JSON.parse(req);
    }


//calll script include and validate for riderssss 

var ridersArray=[];
var variantId=req.variantId;
var variantGr=new GlideRecord("x_rptp_smartinte_0_integrationproductvariants");
variantGr.addQuery("variant_id",variantId+"");
variantGr.query();
if(variantGr.next()){
variantSysId=variantGr.sys_id+"";
}
//if(inputs.isBulk || !inputs.req.rider){
if(!inputs.req.rider){
var riderPerVariantGr=new GlideRecord('x_rptp_smartinte_0_integration_rider_per_variant');
riderPerVariantGr.addQuery("u_variant_id",variantSysId);
riderPerVariantGr.query();
while(riderPerVariantGr.next()){
	var mul=1;
			if(riderPerVariantGr.u_rider_duration_unit=="month"){
				mul=calculatePPT(req.input_frequency);
			}

			var pt=parseInt(req.policyTerm) * mul;
            pt=pt.toString().split(".")[0];

            var ppt=parseInt(req.paymentTerm) * mul;
            ppt=ppt.toString().split(".")[0];

			
            var sumAssured;

            if(req.sumAssured && req.sumAssured!="0"&& req.sumAssured!=0){
                sumAssured=req.sumAssured;
            }
            else{
                if(req.multiplier){
                    sumAssured=parseInt(req.annualPremium) * parseInt(req.multiplier);
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
                else{
                    sumAssured=parseInt(req.annualPremium) * 10;
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
            }
			// if(rider.sumAssured && rider.sumAssured!="" && rider.sumAssured!=null){
			// 	sumAssured=rider.sumAssured;
			// }

			if(riderPerVariantGr.getDisplayValue("u_rider_id") == "RD001"){
				sumAssured=parseInt(sumAssured) * 3;
				sumAssured=sumAssured.toString().split(".")[0]+"";
			}
			if(riderPerVariantGr.u_min_base_sumassured || riderPerVariantGr.u_max_base_sumassured){
				if(riderPerVariantGr.u_min_base_sumassured){
				if(parseInt(riderPerVariantGr.u_min_base_sumassured) <= parseInt(req.sumAssured)){
					var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
			ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					term:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				//ridersArray.push(obj);
				//riderValidator
				var riderValidator = new x_rptp_smartinte_0.Balic_IPG_Rider_Validation();
				var ridersnewObj = riderValidator.validate(obj,riderPerVariantGr.sys_id,req);
				
				//gs.info("RidertestArray " + JSON.stringify(ridersnewArray));
				ridersArray.push(ridersnewObj);


			}
				}
			}

			else if(riderPerVariantGr.u_max_base_sumassured){
				if(parseInt(riderPerVariantGr.u_max_base_sumassured) > parseInt(req.sumAssured)){
					var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
					ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					term:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				//ridersArray.push(obj);
				//riderValidator
				var riderValidator = new x_rptp_smartinte_0.Balic_IPG_Rider_Validation();
				var ridersnewObj = riderValidator.validate(obj,riderPerVariantGr.sys_id,req);
				// if(ridersnewArray){
				// 		if(typeof ridersnewArray=='object'){
				// 			if(ridersnewArray.code){
				// 				// outputs.code="400";
				// 				// outputs.message=scriptIncludeObj.message;
				// 				throw new Error(ridersnewArray.message);
				// 			}
				// 		}
				// 		else{
				// 			var jObj=JSON.parse(ridersnewArray);
				// 			if(jObj.code){
				// 				// outputs.code="400";
				// 				// outputs.message=jObj.message;
				// 				throw new Error(jObj.message);
				// 			}
				// 		}
				// 	}
				ridersArray.push(ridersnewObj);

			}
				}
			}
			}
			else{
			var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
			ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					term:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				//ridersArray.push(obj);
				//riderValidator
				var riderValidator = new x_rptp_smartinte_0.Balic_IPG_Rider_Validation();
				var ridersnewObj = riderValidator.validate(obj,riderPerVariantGr.sys_id,req);
				//gs.info("RidertestArray " + JSON.stringify(ridersnewArray));
				
				ridersArray.push(ridersnewObj);

				}
			}
}

}
else{
	//gs.info("riders "+JSON.stringify(req.rider));
	var invalidRider=[];
	var duprider = [];
    var riders=req.rider;
//Rider Validation
	// var validRiders=[];
	// var invalidRiderFound = false;

    //     var riderPerVariantGr=new GlideRecord('x_rptp_smartinte_0_integration_rider_per_variant');
    //     riderPerVariantGr.addQuery("u_variant_id",variantSysId);
    //     riderPerVariantGr.query();
	// 		//gs.info(riderPerVariantGr.getDisplayValue("u_rider_id"))
	// 		while (riderPerVariantGr.next()) {
   	// 		 validRiders.push(riderPerVariantGr.getDisplayValue("u_rider_id"));
	// 	}
	// 	gs.info("Valid Riders for this variant: "+validRiders);
		
	// 	riders.forEach(function(rider) {
    // 	gs.info("riders ind: " + JSON.stringify(rider));
    
    // 	if (!validRiders.includes(rider.name)) {
    //     	gs.info("Invalid rider: " + rider.name);
    //     	invalidRiderFound = true;
    // 	}
	// 	});
	// if (invalidRiderFound) {
    // outputs.code = 400;
    // outputs.message = "Invalid Rider";
	// }
//ABove is ridwer validation 
    riders.forEach(function(rider){
			gs.info("riders ind"+JSON.stringify(rider));


        var riderPerVariantGr=new GlideRecord('x_rptp_smartinte_0_integration_rider_per_variant');
        riderPerVariantGr.addQuery("u_variant_id",variantSysId);
        riderPerVariantGr.query();
		var flag=false;
        while(riderPerVariantGr.next()){
			//gs.info(riderPerVariantGr.getDisplayValue("u_rider_id")

            if(rider.name==riderPerVariantGr.getDisplayValue("u_rider_id")){
			
				flag=true;
				gs.info(riderPerVariantGr.getDisplayValue("u_rider_id"))
                var mul=1;
				duprider.push(rider.name);//pushing the rider name for validation of duplicates
			if(riderPerVariantGr.getDisplayValue("u_rider_id") == "RD001"){
				mul=3;
			}
			if(riderPerVariantGr.u_rider_duration_unit=="month"){
				mul=calculatePPT(req.input_frequency);
			}
            var pt=parseInt(req.policyTerm) * mul;
            pt=pt.toString().split(".")[0];

            var ppt=parseInt(req.paymentTerm) * mul;
            ppt=ppt.toString().split(".")[0];

            var sumAssured;

            if(req.sumAssured && req.sumAssured!="0"&& req.sumAssured!=0){
                sumAssured=req.sumAssured;
            }
            else{
                if(req.multiplier){
                    sumAssured=parseInt(req.annualPremium) * parseInt(req.multiplier);
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
                else{
                    sumAssured=parseInt(req.annualPremium) * 10;
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
            }
			if(rider.sumAssured && rider.sumAssured!="" && rider.sumAssured!=null){
				sumAssured=rider.sumAssured;
			}
            var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
			ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					term:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				//ridersArray.push(obj);
				//riderValidator
				var riderValidator = new x_rptp_smartinte_0.Balic_IPG_Rider_Validation();
				var ridersnewObj = riderValidator.validate(obj,riderPerVariantGr.sys_id,req);
				
				// if(ridersnewArray)
				// {
				// 	if(typeof ridersnewArray == "object")
				// 	{
				// 		if(ridersnewArray.code){
				// 			throw new Error(ridersnewArray.message);
				// 		}
				// 	}
				// 	else{
				// 			var jObj=JSON.parse(ridersnewArray);
				// 			if(jObj.code){
				// 				// outputs.code="400";
				// 				// outputs.message=jObj.message;
				// 				throw new Error(jObj.message);
				// 			}
				// 		}
				// }
				//gs.info("RidertestArray " + JSON.stringify(ridersnewArray));

				ridersArray.push(ridersnewObj);


				}
            }

        }
		if(flag==false){
		 invalidRider.push(rider.name);
		}
    });
	 	if(invalidRider && invalidRider.length>0){
			invalidRider=invalidRider.join(",");
			outputs.code=400;
			outputs.message="Invalid Riders for the selected product "+invalidRider;
		}
	//gs.info(" Duplicated Rider Array: "+duprider);
	if(duprider && duprider.length>0)
	{
		var checkDups = [];
		var dupArr=[];
		for(var i=0;i<duprider.length;i++){ 
			if(checkDups.includes(duprider[i]))
			{
				 if (!dupArr.includes(duprider[i]))
				{
                	dupArr.push(duprider[i]); 
				// outputs.code = 400
				// outputs.message = "Duplicate Riders Found";
				}
			}
			else
			{
				checkDups.push(duprider[i]);
			}
		}
		if(dupArr && dupArr.length>0)
		{
			outputs.code = 400
			outputs.message = "Duplicate Riders Found: "+dupArr.join(', ');
		}
	}
	
};
req.rider=ridersArray;
// gs.info("in");
// gs.info("geyyvdhjv"+JSON.stringify(req));
outputs.req=JSON.stringify(req);
// gs.info("out");

})(inputs, outputs);
function calculatePPT(freq) {
     // gs.info("Freq: {0}, Premium: {1}", freq, premium);
      switch(freq) {
        case "PF003": return 12;
        case "PF001": return 1; 
        case "PF002": return 6;
        case "PF004": return 3; 
        default: return 0;
      }
}

