sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var list =[];
//gs.info("records:"+JSON.stringify(inputs.records));
inputs.records.query()
while (inputs.records.next()) {
    list.push(inputs.records.sys_id);
  
}
var request=inputs.request;

var arr=[];
list.forEach(function(record){
 
    var inputs={};
    var gr=new GlideRecord('x_rptp_insureme_insuranceproductvariant');
    gr.addQuery('sys_id',record+"");
    gr.query();
    if(gr.next()){
       
        var iGr=new GlideRecord('x_rptp_insureme_insuranceinsurer');
        iGr.addQuery('sys_id',gr.insurer);
        iGr.query();
        if(iGr.next()){
      

		inputs['id'] = gr.code; // String 
		inputs['name'] = gr.name; // String 
		inputs['request'] = request; // String 
		inputs['variant'] = gr.cover_name; // String 
		inputs['variantcode'] = gr.cover_code; // String 
		inputs['vendorname'] = iGr.vendorname; // String 
		inputs['vendorcode'] = iGr.vendorcode; // String 
		inputs['agentcode'] = iGr.agentcode; // String 
		inputs['requestsource'] = iGr.requestsource; // String 
		inputs['insurer'] = iGr.code; // String 
    }
        
    }
   arr.push({req:inputs})
})


 
var url="https://ven06798.service-now.com/api/x_rptp_smartinte_0/bulkasync/test"
gs.info(JSON.stringify(arr));
var res=new AsyncREST().postMessages(url,arr);
outputs.response=res;
gs.info("testing :::   "+JSON.stringify(res));

})(inputs, outputs);

