sys_variable_value.value:
(function execute(inputs, outputs) {
    var objectToValidate = {};
    objectToValidate.productname = inputs.request.productname;
    objectToValidate.productid = inputs.request.productid;
    objectToValidate.variant = inputs.request.variant;
    objectToValidate.vendorName = inputs.request.vendorName;
    objectToValidate.applicationNo = inputs.request.applicationNo;

    outputs.request_obj = objectToValidate;
})(inputs, outputs);

