sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
    var req=inputs.request;
    if(req.productId)
    req=req;
    else
    req=JSON.parse(req);

var variant_sysid;
var insurer_sysid;
var defaultFunds=[]
var insurerGr=new GlideRecord("x_rptp_smartinte_0_integrationinsurer");
insurerGr.addQuery("code",inputs.insurer);
insurerGr.query();
if(insurerGr.next()){
    insurer_sysid=insurerGr.sys_id+"";
}


var variantGr=new GlideRecord("x_rptp_smartinte_0_integrationproductvariants");
variantGr.addQuery("variant_id",req.variantId+"");
variantGr.query();
if(variantGr.next()){
    variant_sysid=variantGr.sys_id+"";
}
var gr = new GlideRecord("x_rptp_smartinte_0_integration_variantfunds");
gr.addQuery("insurer",insurer_sysid);
gr.addQuery("variantid",variant_sysid);
gr.query();
while(gr.next()){
  //  gs.addInfoMessage(gr.getDisplayValue("fundcode"))
    var fGr=new GlideRecord("x_rptp_smartinte_0_integration_fund");
    fGr.addQuery("sys_id",gr.fundcode+"");
    fGr.query();
    if(fGr.next()){
        var obj={
            fundCode:fGr.id+"",
            name:fGr.name+"",
            value:gr.percentage+"",
            name_cd:fGr.code+""
            };
      //  if(gr.getDisplayValue("fundcode"))
        defaultFunds.push(obj);
    }
}


    if(!req.funds){
        req.funds=defaultFunds;

    }else{
        var funds=req.funds;
        var reqFunds=[];
        var invalidFunds = [];
        defaultFunds.forEach(function(fund){
        fund.value="0";
          funds.forEach(function(element){
            if(fund.fundCode==element.name){
                fund.value=element.value+"";
                return;
            }
          });
        
     //     gs.info(JSON.stringify(fund));
          reqFunds.push(fund);
        });


// Throw error if any invalid fund is 
var du=[]
        funds.forEach(function(element) {
        if(du.includes(element.name)){
            invalidFunds.push(element.name);
        }
        else{
            var validFund = defaultFunds.some(function(fund) {
            return fund.fundCode === element.name; 
        });
         if (!validFund) {
            invalidFunds.push(element.name); 
        }
        du.push(element.name);
        }
    });

    if (invalidFunds.length > 0 ) {
        outputs.msg="Invalid fund code: " + invalidFunds.join(", ");
        outputs.code=400
        return
    }

     //gs.info(inputs.insurer)
 
        req.funds=reqFunds;
    }

// if balic send only selected fund
  if(inputs.insurer=="BALIC"){
   
    req.funds = req.funds.filter(function(fund) {
        return fund.value !== "0";  
    });
   }
//end of send selected fund
        

//calculate the pt from entry age 
    if(req.productId=="6017bdb8ee33446abe361de25c2bc9f0"){
        var age=calculateAge(req.dateOfBirth);
        var pt=req.policyTerm ;
        if(parseInt(pt) - parseInt(age)  < 85){
         pt = 85;
        }
        else{
           pt= parseInt(pt) - parseInt(age) ;
        }
        req.policyTerm=pt+"";
    }
 //       gs.info("request "+JSON.stringify(req));
    req.isExCustomer=req.isExistingUser;
    if(!req.isExistingUser){
        req.isExistingUser="false";
    }

    if(!req.isNri){
        req.isNri="false";
    }
    if(!req.gstWaiver){
        req.gstWaiver="false";
    }

    if(!req.isProposer){
        req.isProposer="true";
    }
    if(!req.multiplier && !(req.productId=="0550dee2331e4517ba2624e1a289e695")){
    req.multiplier=10;
}

if(!req.sumAssured){
    req.sumAssured=0;
}


// if(req.variantId=="VRN003"){
//     req.swpFlag="Y";
//     if(!req.swpYears){
//         req.swpYears=req.policyTerm - 4 +"";
//     }
// }
outputs.request=req

})(inputs, outputs);
   function calculateAge(dob) {
  var convertedDate=dob.split("/").reverse().join("/"); // output: 2022/07/24
return  new x_rptp_smartinte_0.moment().diff(convertedDate, 'years');
}


        // if(inputs.insurer == "ICICI"){
        //     req.funds=[{
        //             "name": "Multi Cap Balanced Fund",
        //             "value": "100"
        //         }]
        // }
        // else if(inputs.insurer == "HDFC"){

        //     if(req.variantId=="VRN007" || req.variantId=="VRN008" || req.variantId=="VRN009" || req.variantId=="VRN011"){
        //         req.funds=[{
        //             "name": "Bond Fund",
        //             "value": "100"
        //         }]
        //     }
        //     else if(req.variantId=="VRN010" || req.variantId=="VRN012"){
             
        //     }
        //     else{
        //         req.funds=[{
        //             "name": "Bond Fund",
        //             "value": "100"
        //         }]
        //     }

        // }
        // else if(inputs.insurer == "BALIC"){
        //     req.funds=[{
        //   "name": "ASFII",
        //   "value": "100"
        // }];


        
        // }

