sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
//gs.info("last used"+inputs.lastUsed);
var curApplication=parseInt(inputs.lastUsed) + 1;
//gs.info("cur"+curApplication);

if(!inputs.min || !inputs.max || !inputs.lastUsed){
    outputs.app=inputs.appNum;
}else{
    if(curApplication<parseInt(inputs.min)){
        curApplication=parseInt(inputs.min);
    }
    else if(curApplication>parseInt(inputs.max)){
        curApplication=parseInt(inputs.min);
    }
    else{
        curApplication=curApplication;
    }
        outputs.last_used=curApplication;
        curApplication=curApplication.toString().split(".")[0];
        outputs.save_app_num=curApplication;
}

})(inputs, outputs);

