sys_variable_value.value:
(function execute(inputs, outputs) {
var req = inputs.req;
var object = inputs.object;
var funds = req.funds;
var arr = [];

funds.forEach(function(element){
    arr.push({
        "@id": "ID127",
        "Features": {
            "Fund_Allocation_Percentage": element.value,
            "Fund_Name": element.name
        }
    })
})

var list = {
    "Fund":{
        "Fund": arr
    }
}

object.Fund=list;
outputs.Response=object;
})(inputs, outputs);

