sys_variable_value.value:
(function execute(inputs, outputs) {
var req = inputs.req;
var object = inputs.object;
var rider = req.rider;
var arr=[];
var counter =7;
rider.forEach(function(element){
    counter++;
    arr.push({
        "@id": "",
        "Features": {
            "CI_Option": element.ciOption || {"@NIL": "true"},
            "Rider_Sum_Assured": element.sumAssured,
            "Rider_Type": element.name
        },
        "Links": {
            "Rider-Insured_LIFE_FRAMEWORK": {
                "Insured": {
                    "@id": "ID" + (counter+1),
                    "Features": {
                        "Age": {
                            "@NIL": "true"
                        },
                        "Gender": {
                            "@NIL": "true"
                        },
                        "Marital_Status": {
                            "@NIL": "true"
                        },
                        "Occupation": {
                            "@NIL": "true"
                        },
                        "Smoker_Status": {
                            "@NIL": "true"
                        }
                    }
                }
            }
        }
    })
})

var list = {
    "Rider":{
        "Rider":arr
    }
}
object.Rider = list;

outputs.Response = object;
})(inputs, outputs);

