sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

var req;
if (typeof inputs.req =='object'){
    req=inputs.req;
}
else{
    req=JSON.parse(inputs.req);
}

if (req.pt===req.ppt){
    outputs.ppt = "Regular Pay";
    }
    else{
            outputs.ppt = "Limited Pay";              
    }
      if(req.variantId=="VRN006"){
            if(parseInt(req.age)+parseInt(req.pt) == 99){
            outputs.ppt="Whole Life"
        }
        else{
            outputs.ppt = "Limited Pay"; 
        }
    }
})(inputs, outputs);

