sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

var req;
if (inputs.req.productId){
    req=inputs.req;
}
else{
    req=JSON.parse(inputs.req);
}

req.pptOption=inputs.pptOption;
outputs.req=req;

})(inputs, outputs);

