sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

var req;
if (inputs.req.productId){
    req=inputs.req;
}
else{
    req=JSON.parse(inputs.req);
}
gs.info(JSON.stringify(req)+"input block")
if (req.policyTerm===req.paymentTerm){
    outputs.ppt = "Regular Pay";
    }
    else{
          
            outputs.ppt = "Limited Pay"; 
              
    }
    

      if(req.variantId=="VRN006"){
            if(parseInt(req.age)+parseInt(req.policyTerm) == 99){
            outputs.ppt="Whole Life"
        }
        else{
            outputs.ppt = "Limited Pay"; 
        }
        }
})(inputs, outputs);

