sys_variable_value.value:
(function execute(inputs, outputs) {
    var req=inputs.req;
    if(typeof req =='object'){
        req=req;
    }else{
        req=JSON.parse(req);
    }
// ... code ...
var ridersArray=[];
var variantId=req.variantId;
var variantGr=new GlideRecord("x_rptp_smartinte_0_integrationproductvariants");
variantGr.addQuery("variant_id",variantId+"");
variantGr.query();
if(variantGr.next()){
variantSysId=variantGr.sys_id+"";
}
//if(inputs.isBulk || !inputs.req.rider){
if(!inputs.req.rider){
var riderPerVariantGr=new GlideRecord('x_rptp_smartinte_0_integration_rider_per_variant');
riderPerVariantGr.addQuery("u_variant_id",variantSysId);
riderPerVariantGr.query();
while(riderPerVariantGr.next()){
	var mul=1;
			if(riderPerVariantGr.u_rider_duration_unit=="month"){
				mul=calculatePPT(req.frequency);
			}

			var pt=parseInt(req.policyTerm) * mul;
            pt=pt.toString().split(".")[0];

            var ppt=parseInt(req.paymentTerm) * mul;
            ppt=ppt.toString().split(".")[0];

            var sumAssured;

            if(req.sumAssured && req.sumAssured!="0"&& req.sumAssured!=0){
                sumAssured=req.sumAssured;
            }
            else{
                if(req.multiplier){
                    sumAssured=parseInt(req.annualPremium) * parseInt(req.multiplier);
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
                else{
                    sumAssured=parseInt(req.annualPremium) * 10;
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
            }
			// if(rider.sumAssured && rider.sumAssured!="" && rider.sumAssured!=null){
			// 	sumAssured=rider.sumAssured;
			// }
			if(riderPerVariantGr.u_min_base_sumassured || riderPerVariantGr.u_max_base_sumassured){
				if(riderPerVariantGr.u_min_base_sumassured){
				if(parseInt(riderPerVariantGr.u_min_base_sumassured) <= parseInt(req.sumAssured)){
					var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
			ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					pt:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				ridersArray.push(obj);
			}
				}
			}

			else if(riderPerVariantGr.u_max_base_sumassured){
				if(parseInt(riderPerVariantGr.u_max_base_sumassured) > parseInt(req.sumAssured)){
					var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
					ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					pt:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				ridersArray.push(obj);
			}
				}
			}
			}
			else{
			var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
			ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					pt:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				ridersArray.push(obj);
				}
			}
}

}
else{
	//gs.info("riders "+JSON.stringify(req.rider));
    var riders=req.rider;
    riders.forEach(function(rider){
			//gs.info("riders ind"+JSON.stringify(rider));

        var riderPerVariantGr=new GlideRecord('x_rptp_smartinte_0_integration_rider_per_variant');
        riderPerVariantGr.addQuery("u_variant_id",variantSysId);
        riderPerVariantGr.query();
        while(riderPerVariantGr.next()){
			//gs.info(riderPerVariantGr.getDisplayValue("u_rider_id"))
            if(rider.name==riderPerVariantGr.getDisplayValue("u_rider_id")){
				//gs.info(riderPerVariantGr.getDisplayValue("u_rider_id"))
                var mul=1;
			if(riderPerVariantGr.u_rider_duration_unit=="month"){
				mul=calculatePPT(req.input_frequency);
			}
            var pt=parseInt(req.policyTerm) * mul;
            pt=pt.toString().split(".")[0];

            var ppt=parseInt(req.paymentTerm) * mul;
            ppt=ppt.toString().split(".")[0];

            var sumAssured;

            if(req.sumAssured && req.sumAssured!="0"&& req.sumAssured!=0){
                sumAssured=req.sumAssured;
            }
            else{
                if(req.multiplier){
                    sumAssured=parseInt(req.annualPremium) * parseInt(req.multiplier);
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
                else{
                    sumAssured=parseInt(req.annualPremium) * 10;
                    sumAssured=sumAssured.toString().split(".")[0]+"";
                }
            }
			if(rider.sumAssured && rider.sumAssured!="" && rider.sumAssured!=null){
				sumAssured=rider.sumAssured;
			}
            var ridersGr=new GlideRecord('x_rptp_smartinte_0_integration_riders');
			ridersGr.addQuery('sys_id',riderPerVariantGr.u_rider_id+"");
			ridersGr.query();
			if(ridersGr.next()){
				var obj={
					name:ridersGr.u_code+"",
					pt:pt,
					ppt:ppt,
					sumAssured:sumAssured,
					mul:mul
				};
				ridersArray.push(obj);
				}
            }
        }
    })
};
req.rider=ridersArray;
outputs.req=(req);
})(inputs, outputs);
function calculatePPT(freq) {
     // gs.info("Freq: {0}, Premium: {1}", freq, premium);
      switch(freq) {
        case "PF003": return 12;
        case "PF001": return 1; 
        case "PF002": return 6;
        case "PF004": return 3; 
        default: return 0;
      }
}

