            outputs.message="Minimum Premium for the product is " + calculateMiniPremium(gr.min_premium,req.frequency);
            return;
        }
    }
    if(gr.max_premium){
        if(gr.max_premium<premium){
            outputs.code=400
            //outputs.message="Minimum Annual Premium for the product is " + gr.min_premium;
            outputs.message="Maximum Premium for the product is " + calculateMaxPremium(gr.max_premium,req.frequency);
            return;
        }
    }

    //check for maturity age
    // if(gr.min_maturity_age && gr.max_maturity_age){
    //     if(!(gr.min_maturity_age > age + req.policyterm >gr.max_maturity_age)){
    //         outputs.code=400
    //         outputs.message="Maturity age  for the product is "+gr.min_maturity_age+" - "+gr.max_maturity_age;
    //         return;
    //     }
    // }

    // mini maturity age validation

    

     if(gr.min_maturity_age){
        if( gr.min_maturity_age > age + parseInt(req.policyTerm)){
            // if(req.isProposer=="false"){
            //   //  gs.info(req.isProposer);

            //     var proposeAge=getAge(req.proposerDob);
            //   //  gs.info(proposeAge);

            //     if( gr.min_maturity_age <= proposeAge){
            //        // gs.info("proposeAge");
            //        // return;
            //     }
            //     else{
            //         outputs.code=400
            // outputs.message="Minimum Maturity age for the product is "+gr.min_maturity_age;
            // return;
            //     }
            // }
            // else{
                outputs.code=400
            outputs.message="Minimum Maturity age for the product is "+gr.min_maturity_age +" "+ageOption;
            return;
        //    }
            
        }
    }
    // max maturity age validation
     if(gr.max_maturity_age){
        if( age  + parseInt(req.policyTerm) > gr.max_maturity_age){
        //    gs.info("hi");
            outputs.code=400
            outputs.message="Maximum Maturity age for the product is "+gr.max_maturity_age +" "+ageOption;
            return;
        }
    }
//check for multiplier
    if(gr.min_death_benefit && req.multiplier ){

        if(req.multiplier < gr.min_death_benefit){
            outputs.code=400
            outputs.message="Minimum Death Benefit Multiplier for the product is "+gr.min_death_benefit;
            return;
        }
    }
    if(gr.max_death_benefit && req.multiplier){
        if(req.multiplier > gr.max_death_benefit){
            outputs.code=400
            outputs.message="Maxium Death Benefit Multiplier for the product is "+gr.max_death_benefit;
            return;
        }
    }

  //multiplier validations


//end
    //check for sumAssured

    if((req.sumAssured && req.sumAssured!="") && gr.sum_assured){
        if(gr.sum_assured){
        if(gr.sum_assured>req.sumAssured){
            //gs.info("hey");
            gs.info("sumA: "+gr.sum_assured);
            gs.info("sumAreq: "+req.sumAssured);
            outputs.code=400
            outputs.message="Minimum Sum Assured for the product is "+gr.sum_assured;
            return;
            }
        }
    }
    if((req.sumAssured && req.sumAssured!="") && gr.max_sum_assured){
        if(gr.max_sum_assured){
        if(gr.max_sum_assured<req.sumAssured){
           // gs.info("hey");
            outputs.code=400
            outputs.message="Maximum Sum Assured for the product is "+gr.max_sum_assured;
            return;
            }
        }
    }
    //Validating frequecy allowing only yearly & half yearly frequency
   if(req.variantId=="VRN029")
   {
    if((req.frequency== "PF001")||(req.frequency== "PF004")){
        outputs.code=400
        outputs.message="Invalid Frequency";
        return;
    }
    // gs.info("req sumAssured"+ req.sumAssured);
    // gs.info("rider current sumAssured: "+ req.rider.sumAssured);
   } 


if(req.isProposer=="false"){
   // gs.info("hey");

   if(!req.proposerDob && !req.proposerGender && !req.proposerFName){
        outputs.code=400
            outputs.message="Proposer Details are Mandatory";
            return;
    }
    if(!req.proposerDob && !req.proposerGender){
        outputs.code=400
            outputs.message="Proposer Date of Birth and Gender is Mandatory";
            return;
    }
    if(!req.proposerDob && !req.proposerFName){
        outputs.code=400
            outputs.message="Proposer Date of Birth and First Namr is Mandatory";
            return;
    }
    if(!req.proposerFName && !req.proposerGender){
        outputs.code=400
            outputs.message="Proposer First Name and Gender is Mandatory";
            return;
    }

    
     if(!req.proposerDob){
        outputs.code=400
            outputs.message="Proposer Date of Birth is Mandatory";
            return;
    }

    if(!req.proposerGender){
        outputs.code=400
            outputs.message="Proposer Gender is Mandatory";
            return;
    }
    if(!req.proposerFName){
        outputs.code=400
            outputs.message="Proposer First Name is Mandatory";
            return;
    }
    
}

if(req.isProposer=="false"){
    //gs.info(proposeAge);
    var proposeAge=getAge(req.proposerDob);
    // if(true){
    //     if(18>proposeAge) {
    //         //gs.info("hey");
    //         outputs.code=400
    //         outputs.message="Minimum Proposer Age for the product is "+18;
    //         return;
    //     }
    // }
    // gs.info(proposeAge);
    // gs.info("hii");
    // gs.info(req.variantId);
    gr =new GlideRecord('x_rptp_smartinte_0_integration_values_validator_tables');
    gr.addQuery('variant_id',req.variantId);
    gr.addQuery('insuredperson',false);
    gr.query();
    if(gr.next()){
        ageOption="years";
        if(gr.entry_age_unit=="days"){
        proposeAge=getAgeInDays(req.dateOfBirth);
        ageOption="days";
        }
      //  gs.info("proposre");
        if(gr.min_entry_age){
        if(gr.min_entry_age>proposeAge){
            outputs.code=400
            outputs.message="Minimum Proposer Entry Age for the product is "+gr.min_entry_age +" "+ageOption ;
            return;
        }
    }


     
    outputs.code=200;   


ageOption="years";
 proposeAge=getAge(req.proposerDob);

    if(gr.max_entry_age){
        if(gr.max_entry_age<proposeAge){
            outputs.code=400
            outputs.message="Maximum Proposer Age for the product is "+gr.max_entry_age +" "+ageOption;
            return;
        }
    }
    outputs.code=200;
    }
    }


    
}


//validation for levelCoverPeriod


if(req.levelCoverPeriod){
    if(parseInt(req.paymentTerm) > parseInt(req.levelCoverPeriod) || parseInt(req.levelCoverPeriod) > parseInt(req.policyterm) - 5){
            outputs.code=400
            outputs.message="Level Cover Period Term must be in range of "+req.paymentTerm +" - "+ parseInt(req.policyterm) - 5;
            return;
    }
}

if(req.variantId == "VRN029")
{
    var funds = req.funds;
    var totalFunds = 0;
       if(funds && funds.length>0){
  
        for (var i = 0; i < funds.length; i++) {
            totalFunds += parseInt(funds[i].value);
    }
    if(totalFunds != 100)
    {
        outputs.code = 400
        outputs.message = "Total Fund Percentage Must Be 100";
        return;
    }
    }
    
}

})(inputs, outputs);

