function calculateAnnualPremium(premium, freq) {
     // gs.info("Freq: {0}, Premium: {1}", freq, premium);

      switch(freq) {
        case "PF003": return parseInt(premium);
        case "PF001": return 12 * parseInt(premium); 
        case "PF002": return 2 * parseInt(premium); 
        case "PF004": return 4 * parseInt(premium); 
        default: return 0;
      }
  }

function calculateMiniPremium(premium,freq){
    var min=0;
    switch(freq) {
        case "PF003": min=parseInt(premium) / 1 ;
        break;
        case "PF001": min=parseInt(premium) / 12; 
        break;

        case "PF002": min=parseInt(premium) / 2; 
        break;

        case "PF004": min=parseInt(premium) / 4; 
        break;

        default: min= 0;
        break;

      }
    //  gs.info(min + "beore");
      min =Math.ceil(min);
      min=min.toString().split(".")[0];

    return min;
}


function calculateMaxPremium(premium,freq){
    var max=0;
    switch(freq) {
        case "PF003": max= parseInt(premium) / 1 ;
        break;
        case "PF001": max= parseInt(premium) / 12; 
        break;
        case "PF002": max= parseInt(premium) / 2; 
        break;
        case "PF004": max= parseInt(premium) / 4; 
        break;
        default: max= 0;
        break;
      }
    
    max =Math.ceil(max);
    max=max.toString().split(".")[0];
    return max;

}


var req=inputs.req;
var premium=calculateAnnualPremium(req.premium,req.paymentFrequency)

