function getAge(dateOfBirth){
 // original date string in dd/mm/yyyy format
var convertedDate=dateOfBirth.split("/").reverse().join("/"); // output: 2022/07/24
return  new x_rptp_smartinte_0.moment().diff(convertedDate, 'years');
}



//to fetch the age in days
