        }
    }

  //multiplier validations


//end
    //check for sumAssured

    if((req.sumAssured && req.sumAssured!="") && gr.sum_assured){
        if(gr.sum_assured){
        if(gr.sum_assured>req.sumAssured){
            //gs.info("hey");
            outputs.code=400
            outputs.message="Minimum Sum Assured for the product is "+gr.sum_assured;
            return;
            }
        }
    }
    if((req.sumAssured && req.sumAssured!="") && gr.max_sum_assured){
        if(gr.max_sum_assured){
        if(gr.max_sum_assured<req.sumAssured){
           // gs.info("hey");
            outputs.code=400
            outputs.message="Maximum Sum Assured for the product is "+gr.max_sum_assured;
            return;
            }
        }
    }



if(req.isPolicyHolder=="false"){
   // gs.info("hey");

   if(!req.proposerDob && !req.proposerGender && !req.proposerFirstName){
        outputs.code=400
            outputs.message="Proposer Details are Mandatory";
            return;
    }
    if(!req.proposerDob && !req.proposerGender){
        outputs.code=400
            outputs.message="Proposer Date of Birth and Gender is Mandatory";
            return;
    }
    if(!req.proposerDob && !req.proposerFirstName){
        outputs.code=400
            outputs.message="Proposer Date of Birth and First Namr is Mandatory";
            return;
    }
    if(!req.proposerFirstName && !req.proposerGender){
        outputs.code=400
            outputs.message="Proposer First Name and Gender is Mandatory";
            return;
    }

    
     if(!req.proposerDob){
        outputs.code=400
            outputs.message="Proposer Date of Birth is Mandatory";
            return;
    }

    if(!req.proposerGender){
        outputs.code=400
            outputs.message="Proposer Gender is Mandatory";
            return;
    }
    if(!req.proposerFirstName){
        outputs.code=400
            outputs.message="Proposer First Name is Mandatory";
            return;
    }
    
}

if(req.isPolicyHolder=="false"){
    //gs.info(proposeAge);
    var proposeAge=getAge(req.proposerDob);
    // if(true){
    //     if(18>proposeAge) {
    //         //gs.info("hey");
    //         outputs.code=400
    //         outputs.message="Minimum Proposer Age for the product is "+18;
    //         return;
    //     }
    // }
    // gs.info(proposeAge);
    // gs.info("hii");
    // gs.info(req.variantId);
    gr =new GlideRecord('x_rptp_smartinte_0_integration_values_validator_tables');
    gr.addQuery('variant_id',req.variantId);
    gr.addQuery('insuredperson',false);
    gr.query();
    if(gr.next()){
        ageOption="years";
        if(gr.entry_age_unit=="days"){
        proposeAge=getAgeInDays(req.proposerDob);
        ageOption="days";
        }
      //  gs.info("proposre");
        if(gr.min_entry_age){
        if(gr.min_entry_age>proposeAge){
            outputs.code=400
            outputs.message="Minimum Proposer Entry Age for the product is "+gr.min_entry_age +" "+ageOption ;
            return;
        }
    }


     
    outputs.code=200;   


ageOption="years";
 proposeAge=getAge(req.proposerDob);

    if(gr.max_entry_age){
        if(gr.max_entry_age<proposeAge){
            outputs.code=400
            outputs.message="Maximum Proposer Age for the product is "+gr.max_entry_age +" "+ageOption;
            return;
        }
    }
    outputs.code=200;
    }
    }


    
}


//validation for levelCoverPeriod


if(req.levelCoverPeriod){
    if(parseInt(req.ppt) > parseInt(req.levelCoverPeriod) || parseInt(req.levelCoverPeriod) > parseInt(req.pt) - 5){
            outputs.code=400
            outputs.message="Level Cover Period Term must be in range of "+req.ppt +" - "+ parseInt(req.pt) - 5;
            return;
    }
}

})(inputs, outputs);

