sys_variable_value.value:
(function execute(inputs, outputs) {
    var pdfContent = inputs.pdfContent;
    var tableName = 'x_rptp_smartinte_0_integration_request_log';  // Replace with your table name
    var recordId = inputs.txn;  // Replace with your transaction ID or appropriate record identifier

    var gr = new GlideRecord(tableName);
    if (gr.get(recordId)) {
        var attachment = new GlideSysAttachment();
        var attachmentSysId = attachment.write(gr, 'attachment.pdf', 'application/pdf', pdfContent);

        if (attachmentSysId) {
            var encodedContent = attachment.getContentBase64(attachmentSysId);
            outputs.base64String = encodedContent;
        } else {
            gs.error('Failed to write attachment.');
        }
    } else {
        gs.error('Record not found: ' + recordId);
    }
})(inputs, outputs);

