sys_variable_value.value:
(function execute(inputs, outputs) {
  var req=(inputs.request);
 // gs.info(req.insuredPerson);
if(req.proposerDob){
    var inputDateP =req.dateOfBirth; 
  //var d = "01/01/1980";
var gd = new GlideDate();
gd.setDisplayValue(inputDateP);
req.proposerDob=gd.getByFormat('yyyy-MM-dd'); 
}

  var inputDate =req.dateOfBirth; 
  //var d = "01/01/1980";
req.dateOfBirth=inputDate.split("/").reverse().join("-");  // Use MM for month, not mm

  if(req.variantId=="VR006"){
    if(parseInt(req.age)<49 && parseInt(req.age)>0){
      var sa= req.annualPremium * 7;
    sa=sa.toString().split(".")[0]+"";

    req.sumAssured=sa;
  }else if(parseInt(req.age)>49){
    var sa=req.annualPremium * 5;
    sa=sa.toString().split(".")[0]+"";
    req.sumAssured= sa;
  }
  }else{
    var mul=10;
    if(req.multiplier){
      mul=parseInt(req.multiplier);
    }
    var sa=parseInt(req.annualPremium) * mul;
    if(sa<5000000){
      sa=5000000+"";
    }
    sa=sa.toString().split(".")[0]+"";
    req.sumAssured= sa;
  }

outputs.requestbody=req;
// ... code ...
})(inputs, outputs);

