    function validateRadioButton(answer) {
        return answer === 'Y' || answer === 'N';
    }

    function validateTextLength(text, minLength, maxLength) {
        return text.length < minLength || text.length > maxLength;
    }
 
 function validateNumericLength(number, minLength, maxLength) {
    var numStr = number.toString();
    return numStr.length < minLength || numStr.length > maxLength;
}
    function validateDropdown(selection, allowedValues) {
        return !selection || !allowedValues.includes(selection);
    }
    function validateDate(input) { 
    var datePattern = /^\d{4}-\d{2}-\d{2}$/;
    if (!datePattern.test(input)) {
        return false; 
    }

    var dateObj = new Date(input);

    if (isNaN(dateObj.getTime())) {
        return false;
    }

    var formattedDate = dateObj.toISOString().split('T')[0]; 
    if (formattedDate !== input) {
        return false;
    }

    return true;
}

