if(inputs.req.data)
    var request = inputs.req.data.healthDetails;
else{
    var request =  JSON.parse(inputs.req).data.healthDetails;
}

var txnReq=inputs.txnreq;
if(txnReq.gender){

}else{
    txnReq=JSON.parse(txnReq);
