sys_variable_value.value:
(function execute(inputs, outputs) {
// // ... code ...
if(inputs.res.status)
var res=inputs.res;
else
var res=JSON.parse(inputs.res);
//gs.info("res"+res);

if(inputs.req.productId)
var req=inputs.req
else
var req=JSON.parse(inputs.req)
    var age=calculateAge(req.dateOfBirth);

if( res && res.status && ((res.status).toLowerCase()=="success")){
    // gs.info(res);
    // gs.info(req)
            var key=req.productId+"_"+req.variantId+"_"+req.premium+"_"+req.policyTerm+"_"+req.paymentTerm+"_"+req.frequency+"_"+age;
    var gr=new GlideRecord('x_rptp_smartinte_0_integration_cache_table');
    gr.newRecord();
    gr.key=key;
    gr.requestobj= JSON.stringify(req);
    gr.response=JSON.stringify(res);
    gr.insert();
}
})(inputs, outputs);
 function calculateAge(dob) {
  var convertedDate=dob.split("/").reverse().join("/"); // output: 2022/07/24
return  (new x_rptp_smartinte_0.moment().diff(convertedDate, 'years')).toString().split(".")[0];
}

