sys_variable_value.value:
(function execute(inputs, outputs) {
  try {
    var newResponse = JSON.parse(inputs.response);
    var existingResponse;
    if (!inputs.fvressp || inputs.fvressp.trim() === "") {
      existingResponse = { responses: [] };
    } else {
      existingResponse = JSON.parse(inputs.fvressp);
    }
    if (!existingResponse.responses) {
      existingResponse.responses = [];
    }
    existingResponse.responses.push(newResponse);
    outputs.response = JSON.stringify(existingResponse);
    
    
  } catch (error) {
    gs.error("Error processing request: " + error.message);
    outputs.response = JSON.stringify({ error: error.message });
  }
})(inputs, outputs);

