    outputs.isnri= req.isNri  ||""; // adding default value since isNri is not mandatory input.
    outputs.is_existing_user= req.isExCustomer;
    outputs.proposerGender= req.data.proposerInfos.gender ||"";
  //  outputs.ip = req.isProposer;
  //  outputs.fre = req.frequency;
    outputs.gen = req.data.lifeAssrdInfos.gender;
    outputs.occ = req.occupation;
    outputs.marital = req.maritalStatus;

    //outputs.pro = req.proposalType;
    //outputs.strat = req.product.investmentProtifolio;

/*
    if (req.policyTerm===req.paymentTerm){
    outputs.ppt = "Regular Pay";
    }
    else{
        outputs.ppt = "Limited Pay";
    }

    var funds = [];

   if(!array)
   return
//gs.info("hello")

    array.forEach(function(rec) {
