sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var date=new GlideDate()
var arr=date.toString().split("-")
var inputValue = inputs.isProposer; // Adjust this to match your variable name


outputs.date=arr.reverse().join("/");
if (inputValue === 'Yes') {
    outputValue = 'Y';
} else if (inputValue === 'No') {
    outputValue = 'N';
} else {
    gs.error('Invalid input value: ' + inputValue);
}
outputs.isproposer=outputValue

})(inputs, outputs);

