sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var fundsArray=inputs.req.funds;
var jObj=inputs.jObj;
var array=[];

if( inputs.req.funds && inputs.jObj.body.quotedtls.fund){

fundsArray.forEach(function(element){
    var obj={
        "name":element.name,
        "percent":element.value,
        "name_cd":element.name_cd
    }
    array.push(obj);
})
jObj.body.quotedtls.fund=array;


outputs.response=jObj;
}
else{
    outputs.response=jObj;
}

})(inputs, outputs);

