sys_variable_value.value:
(function execute(inputs, outputs) {
    var arr=[];
    var mul=calculatePPT(inputs.rider.frequency);
    var sumAssured = inputs.rider.sumAssured;
    var pt = inputs.rider.policyTerm;
    var ppt = inputs.rider.paymentTerm;
    var pptOp = inputs.pptOption;
var obj=JSON.parse(inputs.requestBody);

if((!inputs.rider.rider || inputs.rider.rider.length==0) && inputs.rider.productId=="363f0ffb5f804d2bac044f931d9bf41a"){
if(parseInt(pt) + parseInt(inputs.rider.age) >75){
    pt=75-parseInt(inputs.rider.age);
}

   var maxSumAssuredForINAD = 20000000;
   var saForINAD = sumAssured > parseInt(maxSumAssuredForINAD) ? maxSumAssuredForINAD : sumAssured;

   var maxSumAssuredForPPRADC = 10000000;
   var saForPPRADC = sumAssured > parseInt(maxSumAssuredForPPRADC) ? maxSumAssuredForPPRADC : sumAssured;

    var defaultRider=[
    {
    "sumAssured": saForINAD+"",
    "pptopt":pptOp,
    "ppt":ppt,
    "name":"INAD",
    "term":pt+""
    },
    {
    "sumAssured": saForPPRADC+"",
    "pptopt":pptOp,
    "ppt":parseInt(ppt) * mul +"",
    "name":"PPR_ADC",
    "term":parseInt(pt) * mul + ""
    }]

    if(inputs.rider.productId=="363f0ffb5f804d2bac044f931d9bf41a");
    obj.body.quotedtls.rider=defaultRider;
}


if(inputs.rider.variantId == "VRN008"){
    var riders=[{
    "sumAssured": sumAssured +"",
    "pptopt":pptOp,
    "ppt":ppt,
    "name":"EL",
    "term":pt
    }];
     obj.body.quotedtls.rider=riders;
}

if(inputs.rider.rider && inputs.rider.rider.length>0){
    obj.body.rider=[];
  
    inputs.rider.rider.forEach(function(element){
        var obj={};
        var multiplier=1;
        if(element.name=="PPR_ADC"){
            multiplier=mul;
        }
        obj={
        "sumAssured":element.sumAssured+"",
        "pptopt":pptOp,
        "ppt":ppt * multiplier +"",
        "name":element.name,
        "term":pt * multiplier+""
        }
arr.push(obj);

    })
    obj.body.quotedtls.rider=arr;
}


 outputs.response=obj;
})(inputs, outputs);
function calculatePPT(freq, ppt) {
     // gs.info("Freq: {0}, Premium: {1}", freq, premium);
      switch(freq) {
        case "FREQ_1": return 12;
        case "FREQ_4": return 1; 
        case "FREQ_2": return 6;
        case "FREQ_3": return 3; 
        default: return 0;
      }
  }

